class LedMatrix {
    pixelSize;

    constructor(canvas, width = 32, height = 16) {
        this.canvas = canvas;
        this.ctx = this.canvas.getContext('2d');

        this.width = width;
        this.height = height;

        this.pixelSize = 16;

        this.canvas.width = this.width * this.pixelSize;
        this.canvas.height = this.height * this.pixelSize;
    }

    clear() {
        const oldFillStyle = this.ctx.fillStyle;

        this.ctx.fillStyle = '#149204';
        this.ctx.rect(0, 0, this.width * this.pixelSize, this.height * this.pixelSize);
        this.ctx.fill();
        this.ctx.fillStyle = oldFillStyle;
    }

    draw({ colors, pixelData }, offset = { x: 0, y: 0 }) {
        const oldFillStyle = this.ctx.fillStyle;

        for (let y = 0; y < pixelData.length; y++) {
            for (let x = 0; x < pixelData[y].length; x++) {
                if (pixelData[y][x] === -1) continue;

                this.ctx.fillStyle = colors[pixelData[y][x]];
                this.ctx.fillRect((offset.x + x) * this.pixelSize, (offset.y + y) * this.pixelSize, this.pixelSize, this.pixelSize);
            }
        }

        this.ctx.fillStyle = oldFillStyle;
    }
}

