let idx = 0;
let pixelWidth = lm.canvas.width / lm.pixelSize;
let pixelHeight = lm.canvas.height / lm.pixelSize;

let offsetX = 0;

const spriteArr = sprites.kirby.tumble;
const spriteMaxWidth = Math.max(...spriteArr.map(s => Math.max(...s.pixelData.map(r => r.length))));
const spriteMaxHeight = Math.max(...spriteArr.map(s => s.pixelData.length));

const framesPerSecond = 15;

const moveLeftToRight = true;
if (moveLeftToRight) {
    offsetX = 0 - spriteMaxWidth;
} else {
    offsetX = pixelWidth;
}

window.setInterval(() => {
    lm.clear();
    lm.draw(spriteArr[idx], { x: offsetX, y: (pixelHeight - spriteMaxHeight) / 2 });

    idx = (idx + 1) % spriteArr.length;

    if (moveLeftToRight) {
        offsetX = offsetX + 2;
        if (offsetX > pixelWidth + spriteMaxWidth) offsetX = 0 - spriteMaxWidth;
    } else {
        offsetX = offsetX - 2;
        if (offsetX < 0 - spriteMaxWidth) offsetX = lm.canvas.width / lm.pixelSize;
    }
}, 1000 / framesPerSecond);
